import React, { useState } from 'react';

const RegistrationPage = () => {
  const [otpEntered, setOtpEntered] = useState(false);

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '85vh',
  };

  const mainRectangleStyle = {
    width: '180rem',
    height: '70rem',
    flexShrink: 0,
    backgroundColor: '#1962B9',
    display: 'flex',
    padding: '2rem',
    boxSizing: 'border-box',
  };

  const innerRectangleStyle = {
    width: '55rem',
    height: '60rem',
    flexShrink: 0,
    borderRadius: '1.25rem',
    backgroundColor: '#E9F3FF',
    margin: '0 12px', 
  };

  const otpBoxStyle = {
    width: '3rem',
    height: '3rem',
    borderRadius: '0.5rem',
    border: '1px solid #ccc',
    margin: '0 0.5rem',
    textAlign: 'center',
    fontSize: '1.8rem',  
  };

  const handleOtpChange = (event) => {
    if (event.target.value.length === 6) {
      setOtpEntered(true);
    } else {
      setOtpEntered(false);
    }
  };

  return (
    <div style={containerStyle}>
      <div style={mainRectangleStyle}>
        <div style={innerRectangleStyle}>
          <form>
            <h2>Register</h2>
            <label style={{ fontSize: '5.0rem' }}>Email:</label>  
            <input type="email" placeholder="Enter your email" style={{ fontSize: '1.6rem' }} required />  
            <br />
            <label style={{ fontSize: '1.6rem' }}>Mobile No:</label>  
            <input type="tel" placeholder="Enter your mobile number" style={{ fontSize: '1.6rem' }} required /> 
            <div>
              <label style={{ fontSize: '1.6rem' }}>Enter OTP:</label>  
              {[...Array(6)].map((_, index) => (
                <input
                  key={index}
                  style={otpBoxStyle}
                  type="text"
                  maxLength="1"
                  onChange={handleOtpChange}
                />
              ))}
            </div>
            <button type="button" disabled={!otpEntered} style={{ fontSize: '1.6rem' }}>
              Next
            </button>
          </form>
          <p style={{ fontSize: '1.4rem' }}>Already have an account? Login</p> 
        </div>
        <div style={innerRectangleStyle}></div>
        <div style={innerRectangleStyle}></div>
      </div>
    </div>
  );
};

export default RegistrationPage;
