import React, { useState } from 'react';

const RegistrationPage = () => {
  const [otpEntered, setOtpEntered] = useState(false);

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '85vh',
  };

  const mainRectangleStyle = {
    width: '160rem',
    height: '47.0625rem',
    flexShrink: 0,
    backgroundColor: '#1962B9',
    display: 'flex',
    padding: '2rem',
    boxSizing: 'border-box',
  };

  const innerRectangleStyle = {
    width: '37.6875rem',
    height: '42.75rem',
    flexShrink: 0,
    borderRadius: '1.25rem',
    backgroundColor: '#E9F3FF',
    margin: '0 12px', 
    
  };

  const otpBoxStyle = {
    width: '3rem',
    height: '3rem',
    borderRadius: '0.5rem',
    border: '1px solid #ccc',
    margin: '0 0.5rem',
    textAlign: 'center',
    fontSize: '1.5rem',
  };

  const handleOtpChange = (event) => {
    if (event.target.value.length === 6) {
      setOtpEntered(true);
    } else {
      setOtpEntered(false);
    }
  };

  return (
    <div style={containerStyle}>
    <div style={mainRectangleStyle}>
        <div style={innerRectangleStyle}>
          <form style={margin-lef}>
            <h2>Register</h2>
            <label>Email:</label>
            <input type="email" placeholder="Enter your email" required />
            <br />
            <label>Mobile No:</label>
            <input type="tel" placeholder="Enter your mobile number" required />
            <br />
            <div>
              <label>Enter OTP:</label>
              {[...Array(6)].map((_, index) => (
                <input
                  key={index}
                  style={otpBoxStyle}
                  type="text"
                  maxLength="1"
                  onChange={handleOtpChange}
                />
              ))}
            </div>
            <button type="button" disabled={!otpEntered}>
              Next
            </button>
          </form>
          <p>Already have an account? Login</p>
        </div>
        <div style={innerRectangleStyle}></div>
       <div style={innerRectangleStyle}></div>
      </div>

    </div>
  );
};

export default RegistrationPage;
