import React, { useState } from 'react';

const ArticleUpload = () => {
  const [selectedCountry, setSelectedCountry] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  
  
  const handleCountryChange = (e) => {
    setSelectedCountry(e.target.value);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredData = formData.filter((data) => {
    if (selectedCountry && data['Country'] !== selectedCountry) {
      return false;
    }

    const values = Object.values(data);
    for (let i = 0; i < values.length; i++) {
      if (values[i].toLowerCase().includes(searchQuery.toLowerCase())) {
        return true;
      }
    }
    return false;
  }); 


  const [formData, setFormData] = useState([]);
  const [csvHeaders, setCSVHeaders] = useState([]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = (event) => {
      const csvData = event.target.result;
      const [header, ...rows] = csvData.split('\n');
      const headers = header.split(',');

      const data = rows.map((row) => {
        const values = row.split(',');
        return headers.reduce((acc, header, index) => {
          acc[header] = values[index];
          return acc;
        }, {});
      });

      setFormData(data);
      setCSVHeaders(headers);
    };

    reader.readAsText(file);
  };

  const handleChange = (e, index) => {
    const { name, value } = e.target;
    const updatedFormData = [...formData];
    updatedFormData[index][name] = value;
    setFormData(updatedFormData);
  };

  
  return (
    <div>
      <input type="file" accept=".csv" onChange={handleFileUpload} />
      <form onSubmit>
        {formData.map((data, index) => (
          <div key={index}>
            {csvHeaders.map((header) => (
              <React.Fragment key={header}>
                <input
                  type="text"
                  name={header}
                  placeholder={header}
                  value={data[header] || ''}
                  onChange={(e) => handleChange(e, index)}
                />
                <span>{header === 'response' ? data[header] : ''}</span>
              </React.Fragment>
            ))}
            <br />
          </div>
        ))}
        <button type="submit">Submit</button>
      </form>
      <div>
        <label htmlFor="countrySelect">Search Country: </label>
        <select id="countrySelect" onChange={handleCountryChange}>
          <option value="">All</option>
          <option value="USA">USA</option>
          <option value="Canada">Canada</option>
        </select>
        <button onClick={() => alert('Check button clicked')}>Check</button>
      </div>
    </div>
  );
};

export default ArticleUpload;
