import React, { useState } from 'react';
import './booking.css';

const DashboardPage = () => {
  const [showBookArticleForm, setShowBookArticleForm] = useState(false);
  const [showMonthDropdown, setShowMonthDropdown] = useState(false);

  const handleBookArticleButtonClick = () => {
    // Add your logic for handling book article button click
  };

  const handleSelectMonthButtonClick = () => {
    // Add your logic for handling select month button click
  };

  // Placeholder data for the donut pie chart
  const totalConsignments = 100;
  const totalDeclaredValue = 2000000; // Assuming in rupees

  // Placeholder data for counters
  const countersData = [
    { icon: 'Icon for PQ Induction Pending', count: 10 },
    { icon: 'Icon for In Transit', count: 25 },
    { icon: 'Icon for Under Process @Customs', count: 15 },
    { icon: 'Icon for Customs Cleared', count: 30 },
    { icon: 'Icon for Customs Detained', count: 5 },
    { icon: 'Icon for Despatched Out of India', count: 20 },
  ];

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', padding: '2vw' }}>
      <button className='BookArt' onClick={handleBookArticleButtonClick}>
        + Book Article
      </button>
      <button className='selectMonth' onClick={handleSelectMonthButtonClick}>
        <div style={{ marginLeft: '1.1vw' }}>Select Month </div>
        {/* Your SVG code for the dropdown */}
      </button>

      {/* Dashboard analytic components */}
      <div>
        {/* Donut pie chart for Total Consignments and total declared value */}
        <div>
          <h2>Total Consignments: {totalConsignments}</h2>
          <h2>Total Declared Value (in Rs): {totalDeclaredValue}</h2>
          {/* Add your donut pie chart component using totalConsignments and totalDeclaredValue */}
        </div>

        {/* Counters for various statuses */}
        <div>
          {countersData.map((counter, index) => (
            <div key={index}>
              <span>{counter.icon}</span>
              <span>Count: {counter.count}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
