import { useRef } from 'react';
import jsPDF from 'jspdf';
import ReportTemplate from './ReportTemplate';
import React from 'react';

function Receipt() {
  const reportTemplateRef = useRef(null);

  const generatePDF = () => {
    const pdf = new jsPDF();
  
    // Create a div element to hold the content from ReportTemplate
    const contentDiv = document.createElement('div');
  
    // Render the ReportTemplate component to a string of HTML
    const reportTemplateHTML = `
      <div class="receipt-container">
        ${ReactDOMServer.renderToStaticMarkup(<ReportTemplate />)}
      </div>
    `;
  
    // Set the HTML content to the div
    contentDiv.innerHTML = reportTemplateHTML;
  
    // Generate PDF from the HTML content
    pdf.html(contentDiv, {
      callback: () => {
        pdf.save('receipt.pdf');
      }
    });
  };
  
  

  return (
    <div>
      <button className="button" onClick={generatePDF}>
        Generate PDF
      </button>
      <div ref={reportTemplateRef}>
        <ReportTemplate />
      </div>
    </div>
  );
}

export default Receipt;
